function endergebniss = berechnungLagerkraefte(knotenMatrix, elementMatrix, aussenkraefteVektor, lagerVector)v = zeros(rows(knotenMatrix)*2,1)elementIndexVector = cell(1,rows(elementMatrix))for i=1:columns(elementIndexVector)   # Staying in the same row (looping thrugh adjacent array elements) might be more CPU efficient cause  startKnoten = elementMatrix(i,1);  endKnoten   = elementMatrix(i,2);  elementIndexVector(1,i) = [2*startKnoten-1,2*startKnoten,2*endKnoten-1,2*endKnoten];endfor
endergebniss = 123endfunction